import React, { useState, useEffect } from "react";
import Transaction from "./Transaction";

function TransactionsList({ transaction }) {
  const [sortOrder, setSortOrder] = useState("asc");
  const [sortColumn, setSortColumn] = useState(""); // Initially empty to allow toggling

  const [sortedTransactions, setSortedTransactions] = useState([...transaction]);

  useEffect(() => {
    // Set initial state when the component mounts
    setSortedTransactions([...transaction]);
  }, [transaction]);


  const sortByColumn = (columnName) => {
    const sorted = [...sortedTransactions].sort((a, b) => {
      if (sortOrder === "asc") {
        return a[columnName] > b[columnName] ? 1 : -1;
      } else {
        // Adjusted the sorting comparison for descending order
        return a[columnName] < b[columnName] ? 1 : -1;
      }
    });

    setSortedTransactions(sorted);
    setSortOrder(sortOrder === "asc" ? "desc" : "asc");
  };


  const handleSortChange = (columnName) => {
    if (sortColumn === columnName) {
      // If the same column is clicked again, toggle the order
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      // If a different column is clicked, update the state
      setSortColumn(columnName);
      setSortOrder("asc"); // Reset to ascending order
    }

    sortByColumn(columnName);
  };

  const table = sortedTransactions.map((item) => {
    return (
      <Transaction
        key={item.id}
        date={item.date}
        description={item.description}
        category={item.category}
        amount={item.amount}
      />
    );
  });

  return (
    <div>
      <label htmlFor="sort">Sort By:</label>
      <select
        id="sort"
        value={sortColumn}
        onChange={(e) => handleSortChange(e.target.value)}
      >
        <option value="">Select</option>
        <option value="description">Description</option>
        <option value="category">Category</option>
      </select>

      <table className="ui celled striped padded table">
        <tbody>
          <tr>
            <th onClick={() => handleSortChange("description")}>
              <h3 className="ui center aligned header">Description</h3>
            </th>
            <th onClick={() => handleSortChange("category")}>
              <h3 className="ui center aligned header">Category</h3>
            </th>
          </tr>
          {table}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionsList;
