import React, { useState } from "react";
import Transaction from "./Transaction";

function TransactionsList({ transaction }) {
  const [sortOrder, setSortOrder] = useState("asc");
  const [sortColumn, setSortColumn] = useState("date");
  const [sortedTransactions, setSortedTransactions] = useState([...transaction]);

  const sortByColumn = (columnName) => {
    const sorted = [...sortedTransactions].sort((a, b) => {
      if (sortOrder === "asc") {
        return a[columnName] > b[columnName] ? 1 : -1;
      } else {
        return a[columnName] < b[columnName] ? 1 : -1;
      }
    });

    setSortedTransactions(sorted);
    setSortOrder(sortOrder === "asc" ? "desc" : "asc");
  };

  const handleSortChange = (e) => {
    const selectedValue = e.target.value;
    const [column, order] = selectedValue.split("-");
    setSortColumn(column);
    setSortOrder(order);
    sortByColumn(column);
  };

  const table = sortedTransactions.map((item) => {
    return (
      <Transaction
        key={item.id}
        date={item.date}
        description={item.description}
        category={item.category}
        amount={item.amount}
      />
    );
  });

  return (
    <div>
      <label htmlFor="sort">Sort By:</label>
      <select id="sort" value={`${sortColumn}-${sortOrder}`} onChange={handleSortChange}>
        <option value="date-asc">Date (Ascending)</option>
        <option value="date-desc">Date (Descending)</option>
        <option value="description-asc">Description (Ascending)</option>
        <option value="description-desc">Description (Descending)</option>
        <option value="category-asc">Category (Ascending)</option>
        <option value="category-desc">Category (Descending)</option>
        <option value="amount-asc">Amount (Ascending)</option>
        <option value="amount-desc">Amount (Descending)</option>
      </select>

      <table className="ui celled striped padded table">
        <tbody>
          <tr>
            <th onClick={() => sortByColumn("date")}>
              <h3 className="ui center aligned header">Date</h3>
            </th>
            <th onClick={() => sortByColumn("description")}>
              <h3 className="ui center aligned header">Description</h3>
            </th>
            <th onClick={() => sortByColumn("category")}>
              <h3 className="ui center aligned header">Category</h3>
            </th>
            <th onClick={() => sortByColumn("amount")}>
              <h3 className="ui center aligned header">Amount</h3>
            </th>
          </tr>
          {table}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionsList;
